<?php$pflog = gostPathFileLog ('logfile.txt');echo $pflog; echo "\r\n";if (function_exists('gostDecryptBlock')) {	$fn='1M';		if ( $res=gostDecryptBlock( file_get_contents($fn.'.enc')) ) {		echo 'OK';		if ( file_put_contents($fn.'.dec', base64_decode($res)) )			echo ' SAVED';		else			echo ' SAVE ERR';	} else		echo ' ERR:'.gostGetLastError();} else	echo 'Function not present.';echo "\r\n";	?>