<?php$pflog = gostPathFileLog ('logfile.txt');echo $pflog;echo "\r\n";if (function_exists('gostEncryptBlockByCertContent')) {	$fn='1M';	$cn='user';		$content = file_get_contents($fn.'.dat');	$b64content = base64_encode($content);	$cerContent = file_get_contents($cn.'.cer');	$b64cerContent = base64_encode($cerContent);	if ( $res=gostEncryptBlockByCertContent( $b64content, $b64cerContent ) ) {		echo 'OK';		if ( file_put_contents($fn.'.encF', $res) )			echo ' SAVED';		else			echo ' SAVE ERR';	} else		echo 'ERR:'.gostGetLastError();} else	echo 'Function not present.';echo "\r\n";	?>